mapper.update:
UPDATE TBL_USER
SET
   USER_NAME = #{userName},
   USER_PHONE = #{userPhone},
   USER_EMAIL = #{userEmail}
WHERE USER_ID = #{sessionId}

UPDATE LAWYER
SET
   LAWYER_NAME = #{lawyerName},
   LAWYER_PHONE = #{lawyerPhone},
   LAWYER_EMAIL = #{lawyerEmail}
WHERE LAWYER_ID = #{sessionId}

UPDATE LAWYER
SET
   COUNSEL = #{counsel}
WHERE LAWYER_ID = #{lawyerId}

mapper.select:
SELECT B.*, U.USER_NAME FROM BOARD B INNER JOIN TBL_USER U ON B.USER_ID = U.USER_ID WHERE 1=1
AND B.LAWYER_ID = #{sessionId}
AND B.BOARD_STATUS = #{boardStatus}
offset #{page} rows fetch next #{pageSize} rows only

SELECT COUNT(*)
FROM BOARD B
INNER JOIN TBL_USER U ON B.USER_ID = U.USER_ID
WHERE 1=1

    AND B.LAWYER_ID = #{sessionId}


    AND B.BOARD_STATUS = #{boardStatus}

mapper.update:
