mapper.update:
UPDATE TBL_USER
SET
   USER_NAME = #{userName},
   USER_PHONE = #{userPhone},
   USER_EMAIL = #{userEmail}
WHERE USER_ID = #{sessionId}

UPDATE LAWYER
SET
   LAWYER_NAME = #{lawyerName},
   LAWYER_PHONE = #{lawyerPhone},
   LAWYER_EMAIL = #{lawyerEmail}
WHERE LAWYER_ID = #{sessionId}

UPDATE LAWYER
SET
   COUNSEL = #{counsel}
WHERE LAWYER_ID = #{lawyerId}
