mapper.select:
SELECT *
FROM TOTALDOCS
WHERE TOTAL_NO = #{totalNo}

	AND KIND = #{kind}

SELECT * 
FROM TOTALDOCS
WHERE TOTAL_NO < #{totalNo}

	AND KIND = #{kind}

ORDER BY TOTAL_NO DESC
FETCH FIRST 1 ROWS ONLY

SELECT * 
FROM TOTALDOCS
WHERE TOTAL_NO > #{totalNo}

	AND KIND = #{kind}

ORDER BY TOTAL_NO ASC
FETCH FIRST 1 ROWS ONLY

mapper.update:
UPDATE TOTAL_CMT
SET
	CONTENTS = #{contents}
WHERE CMT_NO = #{cmtNo}
