Insert into SYSTEM.BOARD (BOARD_NO,BOARD_TITLE,CONTENTS,USER_ID,CNT,CDATE,UDATE,BOARD_STATUS,LAWYER_ID,LAWYER_REVIEW,USE_PACKAGE) values (1,'신호위반 차량과의 충돌 사고, 과실비율 이의 있습니다','정지 신호를 무시하고 교차로에 진입한 상대 차량과 충돌하였습니다. 블랙박스 영상에도 상대방의 명백한 신호위반이 확인됩니다. 하지만 보험사에서는 제 과실이 30%로 산정되었는데, 수긍하기 어렵습니다. 과실비율 조정이 가능할까요?','user_001',4,to_date('25/04/08','RR/MM/DD'),null,'DELETE','lawyer_001','변호사 의견입니다. 1',null);
Insert into SYSTEM.BOARD (BOARD_NO,BOARD_TITLE,CONTENTS,USER_ID,CNT,CDATE,UDATE,BOARD_STATUS,LAWYER_ID,LAWYER_REVIEW,USE_PACKAGE) values (2,'보행자 보호구역 내 사고, 억울한 판단입니다','아이와 함께 횡단보도를 건너던 중 차량에 치였습니다. 블랙박스 영상을 확인해보면 제가 신호를 준수한 것이 명확히 드러납니다. 하지만 보험사는 보행자 과실이 있다는 입장입니다. 이 판단이 정당한지 확인받고 싶습니다.','user_002',3,to_date('25/04/08','RR/MM/DD'),null,'DONE','lawyer_002','변호사 의견입니다. 2',null);
Insert into SYSTEM.BOARD (BOARD_NO,BOARD_TITLE,CONTENTS,USER_ID,CNT,CDATE,UDATE,BOARD_STATUS,LAWYER_ID,LAWYER_REVIEW,USE_PACKAGE) values (3,'음주운전 차량에 사고를 당했는데 제 과실이 있다니요?','음주운전 차량이 갑자기 차선을 넘어와 제 차량과 충돌했습니다. 블랙박스와 목격자 진술도 확보된 상황입니다. 그런데도 제 과실이 20%라고 합니다. 납득이 가지 않습니다. 이 과실비율은 정당한가요?','user_003',1,to_date('25/04/08','RR/MM/DD'),null,'DONE','lawyer_003','변호사 의견입니다. 3',null);
Insert into SYSTEM.BOARD (BOARD_NO,BOARD_TITLE,CONTENTS,USER_ID,CNT,CDATE,UDATE,BOARD_STATUS,LAWYER_ID,LAWYER_REVIEW,USE_PACKAGE) values (4,'무리한 진로변경 차량과의 사고, 억울한 과실비율','앞차가 방향지시등도 없이 갑자기 끼어들어 사고가 났습니다. 블랙박스에는 당시 상황이 명확하게 찍혀 있습니다. 그런데도 제 과실이 절반이나 된다고 하는데, 조정이 가능한가요?','user_004',4,to_date('25/04/08','RR/MM/DD'),null,'DONE','lawyer_004','변호사 의견입니다. 4',null);
